mapper.select:
        select
        d.*
        from
        t_device d
        left join t_user_device c on (d.device_id = c.device_id)
        where
            c.user_id = #{userId}
        
            and d.acnum = #{device.acnum}
        
        
            and d.nickname = #{device.nickname}
        
        
            and d.device_status = #{device.curFmStatus}
